import { formatDate } from '@angular/common';
import { AfterViewInit, Component, ElementRef, Inject, LOCALE_ID, OnInit, ViewChild } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { Return, ReturnDto } from '../api/models';
import { ReturnRm } from '../api/models/return-rm';
import { ReturnService } from '../api/services';
import { AuthService } from '../auth/auth.service';
import { AppService } from '../app.service';

@Component({
  selector: 'app-return-detail',
  templateUrl: './return-detail.component.html',
  styleUrls: ['./return-detail.component.css']
})
export class ReturnDetailComponent implements OnInit, AfterViewInit {
  @ViewChild('resolvedDropdown', { static: true }) resolvedDropdownRef?: ElementRef<HTMLElement>;
  //form: FormGroup;

  constructor(private route: ActivatedRoute, private returnService: ReturnService, private router: Router, private authService: AuthService,
    private fb: FormBuilder, @Inject(LOCALE_ID) private locale: string, private appService: AppService) {

/*    this.form = this.fb.group({})*/
    

   

    //use again if default resolved dropdown does not work again.
    //this.form.controls['resolved'].setValue("Test", { onlySelf: true });
}

  ngAfterViewInit() {

  }

  returnId: string = 'not loaded';
  batchDateDisable = false;
  return: ReturnRm = {};
  message = 'Something went wrong...';
  showToast = false;


  form = this.fb.group({
    customer: ['', Validators.required],
    product: ['', Validators.required],
    qtyOnDoc: [0],
    qtyReturned: [0],
    batchDate: [''],
    owner: [''],
    fault: [''],
    docNo: ['', Validators.required],
    docDate: ['', Validators.required],
    resolved: [true],
    comment: ['']
  })

  ngOnInit(): void {

    //if (!this.authService.currentUser) {
    //  this.router.navigate(['/register-user'])
    //}


    this.route.paramMap.subscribe(p => this.findReturn(p.get("returnId")));


  }
  
  private findReturn = (returnId: string | null) => {
    this.returnId = returnId ?? 'not passed';

    this.returnService.findReturn({ id: this.returnId }).subscribe(r => this.return = r, this.handleError);
  }

  toggleBatch() {
    this.batchDateDisable = !this.batchDateDisable;

    if (this.batchDateDisable == true && this.return.batchDate?.length! > 1) {
      this.form.controls['batchDate'].disable()
      this.form.controls.batchDate.setValue(null)
    }
    else if (this.batchDateDisable == false && this.return.batchDate?.length! > 1) {
      this.form.controls['batchDate'].enable()
    }
    else if (this.batchDateDisable == true ) {
      this.form.controls['batchDate'].enable()
      this.form.controls.batchDate.setValue(null)
    }
    else if (this.batchDateDisable == false) {
      this.form.controls['batchDate'].disable()
    }
  }

  private handleError = (err: any) => {

    if (err.status == 404) {
      this.router.navigate(['/search-returns'])
    }
      
    console.log("Response Error. Status: ", err.status)
    console.log("Response Error. Status: ", err.statusText)
    console.log(err);
  }

  update() {



    console.log(this.batchDate.value + ' ' + this.docDate.value)

    if (!this.customer.touched || !this.customer.dirty) {
      this.form.controls.customer.setValue(this.return.customer!)
    }
    if ((!this.docDate.touched || !this.docDate.dirty) && this.docDate.value != '') {
      this.form.controls.docDate.setValue(formatDate(this.return.docDate!, 'yyyy-MM-dd', this.locale))
    }
    if (!this.docDate.touched || !this.docDate.dirty && this.docDate.value == null) {
      this.form.controls.docDate.setValue(this.return.docDate!)
    }
    if (!this.product.touched || !this.product.dirty) {
      this.form.controls.product.setValue(this.return.product!)
    }
    if (!this.qtyOnDoc.touched || !this.qtyOnDoc.dirty) {
      this.form.controls.qtyOnDoc.setValue(this.return.qtyOnDoc!)
    }
    if ((!this.batchDate.touched || !this.batchDate.dirty) && this.batchDate.value != null) {
      this.form.controls.batchDate.setValue(formatDate(this.return.batchDate!, 'yyyy-MM-dd', this.locale))
    }
    if (!this.batchDate.touched || !this.batchDate.dirty && this.batchDate.value == null) {
      this.form.controls.batchDate.setValue(this.return.batchDate!)
    }
    if (!this.owner.touched || !this.owner.dirty) {
      this.form.controls.owner.setValue(this.return.owner!)
    }
    if (!this.fault.touched || !this.fault.dirty) {
      this.form.controls.fault.setValue(this.return.fault!)
    }
    if (!this.docNo.touched || !this.docNo.dirty) {
      this.form.controls.docNo.setValue(this.return.docNo!)
    }
    if (!this.qtyReturned.touched || !this.qtyReturned.dirty) {
      this.form.controls.qtyReturned.setValue(this.return.qtyReturned!)
    }
    if (!this.resolved.touched || !this.resolved.dirty) {
      this.form.controls.resolved.setValue(this.return.resolved!)
    }
    if (!this.comment.touched || !this.comment.dirty) {
      this.form.controls.comment.setValue(this.return.comment!)
    }

    let editedReturn: Return = {
      id: this.returnId,
      docDate: this.form.controls.docDate.value!,
      customer: this.form.controls.customer.value!,
      product: this.form.controls.product.value!,
      qtyOnDoc: this.form.controls.qtyOnDoc.value!,
      batchDate: this.form.controls.batchDate.value!,
      owner: this.form.controls.owner.value!,
      fault: this.form.controls.fault.value!,
      docNo: this.form.controls.docNo.value!,
      qtyReturned: this.form.controls.qtyReturned.value!,
      resolved: this.form.controls.resolved.value!,
      comment: this.form.controls.comment.value!

    }


    if (this.form.invalid) {
      //this.form.markAllAsTouched();
      return
    }
    if (this.form.valid && this.form.dirty && this.form.touched) {
      this.message = 'Return updated successfully'
      this.showToast = true
    }
    else if (this.form.valid && !this.form.touched && !this.form.dirty) {
      this.message = 'Nothing updated'
      this.showToast = true
    }
  
    this.returnService.updateReturnReturn({ id: this.returnId, body: editedReturn }).subscribe(_ => { this.appService.setMessage(this.message); this.appService.showToast(this.showToast)},this.handleError)
    this.router.navigate(['/']);
  }

  //getters

  get docDate() {
    return this.form.controls.docDate
  }

  get customer() {
    return this.form.controls.customer
  }

  get product() {
    return this.form.controls.product
  }

  get qtyOnDoc() {
    return this.form.controls.qtyOnDoc
  }

  get batchDate() {
    return this.form.controls.batchDate
  }

  get owner() {
    return this.form.controls.owner
  }

  get fault() {
    return this.form.controls.fault
  }

  get docNo() {
    return this.form.controls.docNo
  }

  get qtyReturned() {
    return this.form.controls.qtyReturned
  }

  get resolved() {
    return this.form.controls.resolved
  }

  get comment() {
    return this.form.controls.comment
  }

}
<h2>Edit</h2>
<hr />

<form [formGroup]="form" (ngSubmit)="update()">
  <div class="center shadow-custom mb-3">
    <div class="container">
      <div class="row">
        <div class="mb-3 col-12">
          <label class="form-label">Customer</label>
          <div *ngIf="customer.invalid && (customer.touched || customer.dirty)" class="invalid-feedback d-block">
            <div *ngIf="customer.errors?.['required']">Customer is required</div>
          </div>
          <div class="input-group">
            <input formControlName="customer" class="form-control" list="customerDataList" value="{{return.customer}}">
            <datalist id="customerDataList">
              <option value="Spar" id="1">
              <option value="Checkers">
              <option value="Shoprite">
              <option value="Engen">
              <option value="Woolworths">
            </datalist>
            <button class="btn btn-outline-secondary" [routerLink]="['/customer-detail']" type="button">+</button>
          </div>
        </div>
      </div>

      <div class="row">
        <div class="mb-3 col-12">
          <label class="form-label">Product</label>
          <div *ngIf="product.invalid && (product.touched || product.dirty)" class="invalid-feedback d-block">
            <div *ngIf="product.errors?.['required']">Product is required</div>
          </div>
          <div class="input-group">
            <input formControlName="product" class="form-control" list="productDatalist" value="{{return.product}}">
            <datalist id="productDatalist">
              <option value="Milk">
              <option value="Bread">
              <option value="Eggs">
            </datalist>
            <button class="btn btn-outline-secondary" [routerLink]="['/product-detail']" type="button">+</button>
          </div>
        </div>
      </div>

      <div class="row">
        <div class="mb-3 col-6">
          <label class="form-label">Qty on Document</label>
          <input formControlName="qtyOnDoc" type="number" min="0" class="form-control" value="{{return.qtyOnDoc}}">
        </div>

        <div class="mb-3 col-6">
          <label class="form-label">Qty Returned</label>
          <input formControlName="qtyReturned" type="number" min="0" class="form-control" value="{{return.qtyReturned}}">
        </div>

      </div>

      <div class="row">
        <div class="col-12">
          <div class="input-group mb-3">
            <div class="input-group-text">
              <span class="me-3">No Batch Date</span>
              <input class="form-check-input mt-0" type="checkbox" #batchChecked (change)="toggleBatch()" [checked]="return.batchDate == null ? true : false">
            </div>
            <input formControlName="batchDate" type="date" #batchDateBox [readonly]="batchChecked.checked == true ? true : false" class="form-control" [value]="return.batchDate | date:'yyyy-MM-dd'">
          </div>
        </div>

      </div>

      <div class="row">
        <div class="mb-3 col-12">
          <label class="form-label">Owner</label>
          <div class="input-group">
            <input formControlName="owner" class="form-control" list="ownerDatalist" value="{{return.owner}}">
            <datalist id="ownerDatalist">
              <option value="John">
              <option value="Mary">
              <option value="Paul">
            </datalist>
            <button class="btn btn-outline-secondary" [routerLink]="['/owner-detail']" type="button">+</button>
          </div>
        </div>
      </div>

      <div class="row">
        <div class="mb-3 col-12">
          <label class="form-label">Fault</label>
          <div class="input-group">
            <input formControlName="fault" class="form-control" list="faultDatalist" value="{{return.fault}}">
            <datalist id="faultDatalist">
              <option value="Mouldy">
              <option value="Sour">
              <option value="Expired">
            </datalist>
            <button class="btn btn-outline-secondary" [routerLink]="['/fault-detail']" type="button">+</button>
          </div>
        </div>
      </div>

      <div class="row">
        <div class="mb-3 col-6">
          <label class="form-label">External Document No.</label>
          <div *ngIf="docNo.invalid && (docNo.touched || docNo.dirty)" class="invalid-feedback d-block">
            <div *ngIf="docNo.errors?.['required']">Document Number is required</div>
          </div>
          <input formControlName="docNo" type="text" class="form-control" value="{{return.docNo}}">
        </div>

        <div class="mb-3 col-6">
          <label class="form-label">Document Date</label>
          <div *ngIf="docDate.invalid && (docDate.touched || docDate.dirty)" class="invalid-feedback d-block">
            <div *ngIf="docDate.errors?.['required']">Document Date is required</div>
          </div>
          <input formControlName="docDate" type="date" class="form-control" [value]="return.docDate | date:'yyyy-MM-dd'">
        </div>
      </div>

      <div class="row">
        <div class="mb-3 col-6">
          <label class="form-label">Investigate / Resolved</label>
          <select formControlName="resolved" class="form-select">
            <option *ngIf="return.resolved == true" value=true>Resolved</option>
            <option *ngIf="return.resolved == false" value=false>Investigate</option>
            <option *ngIf="return.resolved == true" value=false>Investigate</option>
            <option *ngIf="return.resolved == false" value=true>Resolved</option>
          </select>
        </div>
      </div>

      <div class="mb-3 col-12">
        <label class="form-label">Comment</label>
        <textarea formControlName="comment" class="form-control" rows="3" maxlength="255">{{return.comment}}</textarea>
      </div>

      <div class="row">
        <div class="col-md-12 mb-3">
          <button type="button" [routerLink]="['/']" class="btn-outline-danger btn col-6"><i class="fa fa-cancel"></i> Cancel</button>
          <button type="submit" class="btn-outline-success btn col-6"><i class="fa fa-save"></i> Save</button>
        </div>
      </div>

    </div>
  </div>
</form>
