import { Injectable } from '@angular/core';
import { UserService } from '../api/services';

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  constructor(private userService: UserService) { }

  currentUser?: User
  userId? : string

  loginUser(user: User) {
    this.currentUser = user
    this.findUser(this.currentUser)

   
  }

  findUser(user: User) {
    this.userService.findUser({ email: user.email }).subscribe(u => { this.userId = u.id; console.log('The user id is: ' + u.id) } )
  }
}


interface User {
  email: string
}
