import { Component, OnInit } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { debounceTime, distinctUntilChanged, filter, map, Observable, OperatorFunction } from 'rxjs';
import { ProductRm } from '../api/models';
import { ProductService } from '../api/services';

@Component({
  selector: 'app-product-detail',
  templateUrl: './product-detail.component.html',
  styleUrls: ['./product-detail.component.css']
})
export class ProductDetailComponent implements OnInit {

  constructor(private productService: ProductService, private activatedRoute: ActivatedRoute, private router: Router, private fb: FormBuilder) { }

  product: ProductRm = {}
  productId = ''
  productAlreadyExists = 'Product already exists'
  skuAlreadyExists = 'SKU already exists'
  prodIdFromTemplate = ''
  skuFromTemplate = ''

  form = this.fb.group({
    productName: this.product.productName,
    weight: this.product.weight,
    price: this.product.price,
    shortCode: this.product.shortCode
  })

  ngOnInit(): void {
    this.activatedRoute.paramMap.subscribe(r => { this.findProduct(r.get("productId")); console.log(this.product.productName) })
  }


  findProduct = (productId: string | null) => {
    this.productId = productId ?? 'Not passed'
    this.productService.findProduct({ id: productId! }).subscribe(p => this.product = p,this.handleError)
  }

  prodFormatter = (product: ProductRm) => product.productName!

  productList: ProductRm[] = []

  //Gets all products for searchbox
  searchProd: OperatorFunction<string, readonly { id?: string | undefined | null; name?: string | undefined | null }[]> = (text$: Observable<string>) => {
    this.productService.searchProduct().subscribe(p => { this.productList = p; console.log("SearchProd gets called") }, this.handleError)

    return text$.pipe(
      debounceTime(200),
      distinctUntilChanged(),
      filter((term) => term.length >= 2),
      map((term) => this.productList.filter((product) => new RegExp(term, 'mi').test(product.productName!)).slice(0, 10))
    );
  }

  private handleError = (err: any) => {

    if (err.status == 404) {
      this.router.navigate(['/products'])
    }

    console.log("Response Error. Status: ", err.status)
    console.log("Response Error. Status: ", err.statusText)
    console.log(err);
  }

  update =()=> {

    if (this.prodIdFromTemplate == this.productAlreadyExists || this.skuFromTemplate == this.skuAlreadyExists)
      return

    console.log(
      'id :' + this.product.id +
      ' productName :' +this.form.controls.productName.value! +
      ' price :' +this.form.controls.price.value! +
      ' weight :' +this.form.controls.weight.value! +
      ' shortCode :'+ this.form.controls.shortCode.value
      )

    let editedProduct: ProductRm = {
      id: this.product.id,
      productName: this.form.controls.productName.value!,
      price: this.form.controls.price.value!,
      weight: this.form.controls.weight.value!,
      shortCode: this.form.controls.shortCode.value!
    }

  }

  productExists(modelName: string) {
    this.prodIdFromTemplate = ''
    if (this.product.productName != modelName) {
      let productFound = this.productList.filter(p => p.productName == modelName)
      if (productFound.length > 0) {
        this.prodIdFromTemplate = this.productAlreadyExists
      }
    }
      
  }

  skuExists(sku: string) {
    this.skuFromTemplate = ''
    if (this.product.shortCode != sku) {
      let skuFound = this.productList.filter(s => s.shortCode == sku)
      if (skuFound.length > 0) {
        this.skuFromTemplate = this.skuAlreadyExists
      }
    }
  }

}
<h3>Edit Product</h3>
<hr />

<div class="center shadow-custom mb-3">
  <div class="container">
    <form [formGroup]="form" (ngSubmit)="update()">

      <div class="row">
        <div class="mb-3 col-12">
          <label class="form-label">Product Name</label>
          <div *ngIf="prodIdFromTemplate == productAlreadyExists" class="invalid-feedback d-block">{{prodIdFromTemplate}}</div>
          <div class="input-group">
            <input #prodValue
                   type="text"
                   class="form-control"
                   [ngbTypeahead]="searchProd"
                   [inputFormatter]="prodFormatter"
                   [resultFormatter]="prodFormatter"
                   [editable]="false"
                   formcontrolName="product"
                   value="{{product.productName}}"
                   (blur)="productExists(prodValue.value)" />
          </div>
        </div>
      </div>

      <div class="row">
        <div class="mb-3 col-6">
          <label class="form-label">Price</label>
          <div class="input-group mb-3">
            <div class="input-group-text">
              <span class="me-3">R</span>
            </div>
            <input type="number" step="0.5" min="0" class="form-control" value="{{product.price | number}}">
          </div>
        </div>

        <div class="mb-3 col-6">
          <label class="form-label">Weight</label>
          <div class="input-group mb-3">
            <input type="number" step="0.5" min="0" class="form-control" value="{{product.weight}}">
            <div class="input-group-text">
              <span class="me-3">Kg</span>
            </div>
          </div>
        </div>
      </div>

      <div class="row">
        <div class="mb-3 col-12">
          <label class="form-label">SKU</label>
          <div *ngIf="skuFromTemplate == skuAlreadyExists" class="invalid-feedback d-block">{{skuFromTemplate}}</div>
          <input #skuValue
                 type="text"
                 class="form-control"
                 value="{{product.shortCode}}"
                 (blur)="skuExists(skuValue.value)">
        </div>
      </div>

      <div class="row">
        <div class="col-md-12 mb-3">
          <button type="button" [routerLink]="['/products']" class="btn-outline-danger btn col-6"><i class="fa fa-cancel"></i> Cancel</button>
          <button type="submit" class="btn-outline-success btn col-6"><i class="fa fa-save"></i> Save</button>
        </div>
      </div>

</form>
  </div>
</div>
