import { Injectable } from '@angular/core';
import { UserService } from '../api/services';

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  constructor(private userService: UserService) { }

  currentUser?: User
  userId? : string

  loginUser = (user: User) => {
    this.currentUser = user
    this.findUser(user)
/*    console.log('The user id is from loginUser method: ' + this.userId)*/
   
  }

  findUser = (user: User) => {
    var userId = 'Not assigned'
    this.userService.findUser({ email: user.email }).subscribe(u => { userId = u.id!; })
    this.userId = userId
    console.log('The user id in the function is: ' + userId)
    console.log('The user id as the global variable is: ' + this.userId)
  }
}


interface User {
  email: string
}
