import { Injectable } from '@angular/core';
import { UserService } from '../api/services';

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  constructor(private userService: UserService) { }

  currentUser?: User
  userId? : string

  loginUser = (user: User) => {
    this.currentUser = user
    this.userService.findUser({ email: user.email }).subscribe(u => {
      this.userId = u.id!;
      this.findUser();
    })
  }

  findUser(): string {
    console.log(this.userId)
    return this.userId
  }
}


interface User {
  email: string
}
