import { Component, OnInit } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { UserService } from '../api/services';
import { AuthService } from '../auth/auth.service';

@Component({
  selector: 'app-register-user',
  templateUrl: './register-user.component.html',
  styleUrls: ['./register-user.component.css']
})
export class RegisterUserComponent implements OnInit {

  constructor(private userService: UserService, private fb: FormBuilder, private authService: AuthService, private router: Router, private activatedRoute: ActivatedRoute) { }

  requestedUrl?: string = undefined

  form = this.fb.group({
    email: [''],
    firstName: [''],
    lastName: ['']
    })

  ngOnInit(): void {
    this.activatedRoute.params.subscribe(p => this.requestedUrl = p['requestedUrl'])
  }

  checkUser(): void {
    const params = { email: this.form.get('email')?.value! }

    //this.userService.findUser(params).subscribe(
    //  this.login, e => {
    //    if(e.status != 404)
    //    console.error(e)
    //  }
    //)
  }

  register() {
    this.userService.registerUser({ body: this.form.value }).subscribe(this.login,
      console.error)
  }

  private login = () => {
    this.authService.loginUser({ email: this.form.get('email')?.value! })
    this.router.navigate([this.requestedUrl ?? '/search-returns'])
  }

}
<form [formGroup]="form">
  <div class="container d-grid gap-3" style="max-width: 700px;">

    <div class="form-floating">
      <input (blur)="checkUser()" formControlName="email" placeholder="Email" class="form-control" type="text" />
      <label for="email">Email</label>

    </div>
    <div class="d-grid gap-3">

      <div class="form-floating">

        <input formControlName="firstName" class="form-control" type="text" id="firstName" placeholder="First Name" />
        <label class="form-label" for="firstName">First Name</label>

      </div>

      <div class="form-floating">

        <input formControlName="lastName" placeholder="Last Name" class="form-control" id="lastName" type="text" />
        <label class="form-label" for="lastName">Last Name</label>

      </div>

    </div>
    <div class="d-flex justify-content-center center">
      <button (click)="register()" type="submit" class="btn btn-primary">Login <i class="fa fa-sign-in" aria-hidden="true"></i></button>
    </div>
  </div>
</form>
