import { Component, OnInit } from '@angular/core';
import { FormBuilder} from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { OwnerRm } from '../api/models';
import { OwnerService } from '../api/services';

@Component({
  selector: 'app-owner-detail',
  templateUrl: './owner-detail.component.html',
  styleUrls: ['./owner-detail.component.css']
})
export class OwnerDetailComponent implements OnInit {

  constructor(private ownerService: OwnerService, private activatedRoute: ActivatedRoute, private fb: FormBuilder) { }

  ownerId = ''
  owner: OwnerRm = {}

  form = this.fb.group({
    firstName: [''],
    lastName: ['']
  })

  ngOnInit(): void {
    this.activatedRoute.paramMap.subscribe(f => this.findOwner(f.get("ownerId")))
  }

  findOwner = (faultId: string | null) => {
    this.ownerService.findOwner({id : faultId!}).subscribe(o => this.owner = o)
  }

  update() {
    console.log('First Name: ' + this.getFormFirstName)
    console.log('Last Name: ' + this.getFormLastName)
  }

  //getters
  get getFormFirstName() {
    return this.form.controls.firstName
  }

  get getFormLastName() {
    return this.form.controls.lastName
  }

}
<h3>Edit Owner</h3>
<hr />

<div class="center shadow-custom mb-3">
  <div class="container">
    <form [formGroup]="form" (ngSubmit)="update()">

      <div class="row">
        <div class="mb-3 col-12">
          <label class="form-label">First Name</label>
          <div class="input-group">
            <input #prodValue
                   type="text"
                   class="form-control"
                   formControlName="productName"
                   value="{{owner.firstName}}"/>
          </div>
        </div>
      </div>

      <div class="row">
        <div class="mb-3 col-12">
          <label class="form-label">Last Name</label>
          <input #shortCodeValue
                 type="text"
                 class="form-control"
                 value="{{owner.lastName}}"
                 formControlName="lastName">
        </div>
      </div>

      <div class="row">
        <div class="col-md-12 mb-3">
          <button type="button" [routerLink]="['/products']" class="btn-outline-danger btn col-6"><i class="fa fa-cancel"></i> Cancel</button>
          <button type="submit" class="btn-outline-success btn col-6"><i class="fa fa-save"></i> Save</button>
        </div>
      </div>

    </form>
  </div>
</div>
