import { Component, OnInit } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { debounceTime, distinctUntilChanged, filter, map, Observable, OperatorFunction } from 'rxjs';
import { CustomerRm, FaultRm, OwnerRm, ProductRm, ReturnDto, ReturnRm } from '../api/models';
import { CustomerService, FaultService, OwnerService, ProductService, ReturnService, UserService } from '../api/services';
import { AppService } from '../app.service';

@Component({
  selector: 'app-add-new',
  templateUrl: './add-new.component.html',
  styleUrls: ['./add-new.component.css']
})
export class AddNewComponent implements OnInit {

  batchDateToggle = false;

  constructor(private fb: FormBuilder, private returnService: ReturnService, private router: Router, private appService: AppService, private userService: UserService,
    private customerService: CustomerService, private productService: ProductService, private faultService: FaultService, private ownerService: OwnerService) { }

  customer: CustomerRm = {}
  product: ProductRm = {}
  fault: FaultRm = {}
  owner: OwnerRm = {}
  custIdFromTemplate = ''
  prodIdFromTemplate = ''
  faultIdFromTemplate = ''
  ownerIdFromTemplate = ''

  customerList: CustomerRm[] = []
  productList: ProductRm[] = []
  faultList: FaultRm[] = []
  ownerList: OwnerRm[] = []

  notFound = 'Not Found'
  notLoaded = 'Not Loaded'
  formInvalid = ''

  ngOnInit(): void {
  }

  form = this.fb.group({
    customer: ['', Validators.required],
    product: ['', Validators.required],
    qtyOnDoc: [0],
    qtyReturned: [0],
    batchDate: [],
    owner: [''],
    fault: [''],
    docNo: ['', Validators.required],
    docDate: [,Validators.required],
    resolved: [],
    comment: ['']
  })

  showToast = false;
  message = 'Something went wrong'

  custFormatter = (customer: CustomerRm) => customer.customerName!
  prodFormatter = (product: ProductRm) => product.productName!
  faultFormatter = (fault: FaultRm) => fault.name!
  ownerFormatter = (owner: OwnerRm) => owner.firstName!

  //Gets all customers for searchbox
  searchCust: OperatorFunction<string, readonly { id?: string | undefined | null; name?: string | undefined | null }[]> = (text$: Observable<string>) => {
    this.customerService.searchCustomer().subscribe(c => this.customerList = c, this.handleError)

    return text$.pipe(
      debounceTime(200),
      distinctUntilChanged(),
      filter((term) => term.length >= 2),
      map((term) => this.customerList.filter((customer) => new RegExp(term, 'mi').test(customer.customerName!)).slice(0, 10))
    );
  }

  //Gets all products for searchbox
  searchProd: OperatorFunction<string, readonly { id?: string | undefined | null; name?: string | undefined | null }[]> = (text$: Observable<string>) => {
    this.productService.searchProduct().subscribe(p => this.productList = p, this.handleError)

    return text$.pipe(
      debounceTime(200),
      distinctUntilChanged(),
      filter((term) => term.length >= 2),
      map((term) => this.productList.filter((product) => new RegExp(term, 'mi').test(product.productName!)).slice(0, 10))
    );
  }

  //Gets all faults for searchbox
  searchFault: OperatorFunction<string, readonly { id?: string | undefined | null; name?: string | undefined | null }[]> = (text$: Observable<string>) => {
    this.faultService.searchFault().subscribe(f => this.faultList = f, this.handleError)

    return text$.pipe(
      debounceTime(200),
      distinctUntilChanged(),
      filter((term) => term.length >= 2),
      map((term) => this.faultList.filter((fault) => new RegExp(term, 'mi').test(fault.name!)).slice(0, 10))
    );
  }

  //Gets all owners for searchbox
  searchOwner: OperatorFunction<string, readonly { id?: string | undefined | null; name?: string | undefined | null }[]> = (text$: Observable<string>) => {
    this.ownerService.searchOwner().subscribe(o => this.ownerList = o, this.handleError)

    return text$.pipe(
      debounceTime(200),
      distinctUntilChanged(),
      filter((term) => term.length >= 2),
      map((term) => this.ownerList.filter((owner) => new RegExp(term, 'mi').test(owner.firstName!)).slice(0, 10))
    );
  }

  //Gets the id and name of the model in the search criteria
  getModel(modelName: string, type: string) {
    let modelFound: any[] = []

    //Get the type of model
    if (type == 'customerType') {
      let customerFound = this.customerList.filter(c => c.customerName == modelName)
      console.log(customerFound.length)
      if (customerFound.length > 0) {
        customerFound.map(c => { this.custIdFromTemplate = c.id!; this.customer.customerName = c.customerName! })
      }
      else {
        this.custIdFromTemplate = this.notFound
      }
    }

    if (type == 'productType') {
      let productFound = this.productList.filter(p => p.productName == modelName)
      if (productFound.length > 0) {
        productFound.map(p => { this.prodIdFromTemplate = p.id!; this.product.productName = p.productName! })
      }
      else {
        this.prodIdFromTemplate = 'Not Found'
      }
    }
    if (type == 'faultType') {
      let faultFound = this.faultList.filter(f => f.name == modelName)
      if (faultFound.length > 0) {
        faultFound.map(f => { this.faultIdFromTemplate = f.id!; this.fault.name = f.name! })
      }
      else {
        this.faultIdFromTemplate = 'Not Found'
      }
    }
    if (type == 'ownerType') {
      let ownerFound = this.ownerList.filter(o => o.firstName == modelName)
      if (ownerFound.length > 0) {
        ownerFound.map(o => { this.ownerIdFromTemplate = o.id!; this.owner.firstName = o.firstName! })
      }
      else {
        this.ownerIdFromTemplate = 'Not Found'
      }
    }


  }



  toggleBatch() {
    this.batchDateToggle = !this.batchDateToggle;
  }

  save() {
    if (this.form.invalid) {
      this.form.markAllAsTouched()
      return
    }

    this.message = 'Return Saved'
    this.showToast = true

    const createdReturn: ReturnDto = {
      docDate: this.form.get('docDate')?.value!,
      customerId: this.custIdFromTemplate,
      productId: this.prodIdFromTemplate,
      qtyOnDoc: this.form.get('qtyOnDoc')?.value!,
      batchDate: this.form.get('batchDate')?.value!,
      ownerId: this.ownerIdFromTemplate,
      faultId: this.faultIdFromTemplate,
      docNo: this.form.get('docNo')?.value!,
      qtyReturned: this.form.get('qtyReturned')?.value!,
      resolved: JSON.parse(this.form.controls.resolved.value!),
      comment: this.form.get('comment')?.value!
    }



    this.returnService.createReturnReturn({ body: createdReturn }).subscribe(_ => { this.appService.setMessage(this.message); this.appService.showToast(this.showToast) }, this.handleError)
    this.router.navigate(['/search-returns'])
  }

  private handleError = (err: any) => {

    if (err.status == 404) {
      this.router.navigate(['/search-returns'])
    }

    console.log("Response Error. Status: ", err.status)
    console.log("Response Error. Status: ", err.statusText)
    console.log(err);
  }

  //getters
  get getDocDate() {
    return this.form.controls.docDate
  }

  get getCustomer() {
    return this.form.controls.customer
  }

  get getProduct() {
    return this.form.controls.product
  }

  get getQtyOnDoc() {
    return this.form.controls.qtyOnDoc
  }

  get getBatchDate() {
    return this.form.controls.batchDate
  }

  get getOwner() {
    return this.form.controls.owner
  }

  get getFault() {
    return this.form.controls.fault
  }

  get getDocNo() {
    return this.form.controls.docNo
  }

  get getQtyReturned() {
    return this.form.controls.qtyReturned
  }

  get getResolved() {
    return this.form.controls.resolved
  }

  get getComment() {
    return this.form.controls.comment
  }

}

<h2>Edit</h2>
<hr />

<form [formGroup]="form" (ngSubmit)="save()">
  <div class="center shadow-custom mb-3">
    <div class="container">

      <div class="row">
        <div class="mb-3 col-12">
          <label class="form-label">Customer</label>
          <div *ngIf="custIdFromTemplate == 'Not Found'" class="invalid-feedback d-block">{{custIdFromTemplate}}</div>
          <div class="input-group">
            <input #customerValue
                   type="text"
                   class="form-control"
                   [ngbTypeahead]="searchCust"
                   [inputFormatter]="custFormatter"
                   [resultFormatter]="custFormatter"
                   [editable]="true"
                   formcontrolName="getCustomer"
                   (blur)="getModel(customerValue.value, 'customerType')" />
            <button class="btn btn-outline-secondary" [routerLink]="['/customer-detail']" type="button">+</button>
          </div>
        </div>
      </div>

      <div class="row">
        <div class="mb-3 col-12">
          <label class="form-label">Product</label>
          <div *ngIf="custIdFromTemplate == 'Not Found'" class="invalid-feedback d-block">{{prodIdFromTemplate}}</div>
          <div *ngIf="getProduct.invalid && (getProduct.touched || getProduct.dirty)" class="invalid-feedback d-block">
            <div *ngIf="getProduct.errors?.['required']">Product is required</div>
          </div>
          <div class="input-group">
            <input #prodValue
                   type="text"
                   class="form-control"
                   [ngbTypeahead]="searchProd"
                   [inputFormatter]="prodFormatter"
                   [resultFormatter]="prodFormatter"
                   [editable]="false"
                   formcontrolName="product"
                   (blur)="getModel(prodValue.value, 'productType')" />
            <button class="btn btn-outline-secondary" [routerLink]="['/product-detail']" type="button">+</button>
          </div>
        </div>
      </div>


      <div class="row">
        <div class="mb-3 col-6">
          <label class="form-label">Qty on Document</label>
          <input formControlName="qtyOnDoc" type="number" min="0" class="form-control">
        </div>

        <div class="mb-3 col-6">
          <label class="form-label">Qty Returned</label>
          <input formControlName="qtyReturned" type="number" min="0" class="form-control">
        </div>

      </div>

      <div class="row">
        <div class="col-12">
          <div class="input-group mb-3">
            <div class="input-group-text">
              <span class="me-3">No Batch Date</span>
              <input class="form-check-input mt-0" type="checkbox" #batchChecked (change)="toggleBatch(); batchDateBox.value = ''">
            </div>
            <input formControlName="batchDate" type="date" #batchDateBox [readonly]="batchChecked.checked == true ? true : false" class="form-control">
          </div>
        </div>

        <div class="row">
          <div class="mb-3 col-12">
            <label class="form-label">Owner</label>
            <div *ngIf="custIdFromTemplate == 'Not Found'" class="invalid-feedback d-block">{{ownerIdFromTemplate}}</div>
            <div *ngIf="getOwner.invalid && (getOwner.touched || getOwner.dirty)" class="invalid-feedback d-block">
              <div *ngIf="getOwner.errors?.['required']">Owner is required</div>
            </div>
            <div class="input-group">
              <input #ownerValue
                     type="text"
                     class="form-control"
                     [ngbTypeahead]="searchOwner"
                     [inputFormatter]="ownerFormatter"
                     [resultFormatter]="ownerFormatter"
                     [editable]="false"
                     formcontrolName="owner"
                     (blur)="getModel(ownerValue.value, 'ownerType')" />
              <button class="btn btn-outline-secondary" [routerLink]="['/owner-detail']" type="button">+</button>
            </div>
          </div>
        </div>

        <div class="row">
          <div class="mb-3 col-12">
            <label class="form-label">Fault</label>
            <div *ngIf="custIdFromTemplate == 'Not Found'" class="invalid-feedback d-block">{{faultIdFromTemplate}}</div>
            <div *ngIf="getFault.invalid && (getFault.touched || getFault.dirty)" class="invalid-feedback d-block">
              <div *ngIf="getFault.errors?.['required']">Fault is required</div>
            </div>
            <div class="input-group">
              <input #faultValue
                     type="text"
                     class="form-control"
                     [ngbTypeahead]="searchFault"
                     [inputFormatter]="faultFormatter"
                     [resultFormatter]="faultFormatter"
                     [editable]="false"
                     formcontrolName="fault"
                     (blur)="getModel(faultValue.value, 'faultType')" />
              <button class="btn btn-outline-secondary" [routerLink]="['/fault-detail']" type="button">+</button>
            </div>
          </div>
        </div>


        <div class="row">
          <div class="mb-3 col-6">
            <label class="form-label">External Document No.</label>
            <div *ngIf="getDocNo.invalid && (getDocNo.touched || getDocNo.dirty)" class="invalid-feedback d-block">
              <div *ngIf="getDocNo.errors?.['required']">Document Number is required</div>
            </div>
            <input formControlName="docNo" type="text" class="form-control">
          </div>

          <div class="mb-3 col-6">
            <label class="form-label">Document Date</label>
            <div *ngIf="getDocDate.invalid && (getDocDate.touched || getDocDate.dirty)" class="invalid-feedback d-block">
              <div *ngIf="getDocDate.errors?.['required']">Document Date is required</div>
            </div>
            <input formControlName="docDate" type="date" class="form-control">
          </div>
        </div>

        <div class="row">
          <div class="mb-3 col-6">
            <label class="form-label">Investigate / Resolved</label>
            <select formControlName="resolved" class="form-select">
              <option value=true>Resolved</option>
              <option value=false>Investigate</option>
            </select>
          </div>
        </div>

        <div class="mb-3 col-12">
          <label class="form-label">Comment</label>
          <textarea formControlName="comment" class="form-control" rows="3" maxlength="255"></textarea>
        </div>

        <div class="invalid-feedback d-block mb-3" *ngIf="formInvalid.length > 0">
          {{formInvalid}}
        </div>

        <div class="row">
          <div class="col-md-12 mb-3">
            <button type="button" [routerLink]="['/']" class="btn-outline-danger btn col-6"><i class="fa fa-cancel"></i> Cancel</button>
            <button type="submit" class="btn-outline-success btn col-6"><i class="fa fa-save"></i> Save</button>
          </div>
        </div>

      </div>
    </div>
  </div>
</form>
